import { Component } from '@angular/core';
import { CartService } from '../services/cart.service';
import { Cart } from '../shared/models/Cart';
import { CartItem } from '../shared/models/Cartitem';
import { FoodService } from '../services/food/food.service';

@Component({
  selector: 'app-cart-page',
  templateUrl: './cart-page.component.html',
  styleUrls: ['./cart-page.component.css']
})
export class CartPageComponent {
  cart!: Cart;
  constructor(private cartService: CartService,private foodService:FoodService){
    let foods = foodService.getAll();
    cartService.addtToCart(foods[1]);
    cartService.addtToCart(foods[3]);
    cartService.addtToCart(foods[5]);
    this.setCart();
  }
  ngOnInit():void{}
  setCart(){
    this.cart = this.cartService.getCart();

  }
  removeFromCart(cartItem: CartItem){
    this.cartService.removeFromCart(cartItem.food.id);
    this.setCart();
  }
  changeQuantity(cartItem:CartItem,quantityInString:string){
    const quantity = parseInt(quantityInString);
    this.cartService.changeQuantity(cartItem.food.id,quantity);
    this.setCart();
  }
}
div
<ul>
    <li *ngFor="let cartItem of cart.items">
        <div>
        <img [src]="cartItem.food.imageUrl" [alt]="cartItem.food.name">
    </div>
        <div>
            <a routerLink="./food/cartItem.food.id">
                {{cartItem.food.name}}
            </a>
        </div>
        <div>
            <select #quantitySelect (change)="changeQuantity(cartItem,quantitySelect.value)">
                <option>1</option>
                <option>2</option>
                <option>3</option>
                <option>4</option>
                <option>5</option>

            </select>
        </div>
        <div>
            ₹{{cartItem.price}}.00
        </div>
        <div>
            <button class="remove-button" (click)="removeFromCart(cartItem)">Remove</button>
        </div>
    </li>
</ul>
<div class="checkout">
    <div class="food-count">
       {{cart.items.length}}
    </div>
    <div class="total-price">
        {{cart.totalPrice}}
     </div>
</div>
